import React from 'react';
import { View, StyleSheet, Image, Text, ImageBackground } from 'react-native';



export default function HomePage () {
  return (
    <View style={styles.container}>

      

   <Text style={styles.text1}> FIQUE À VONTADE PARA COMPARTILHAS SEUS TESTEMUNHOS! </Text>

   <Text style={styles.text2}> SOBRE O APLICATIVO </Text>

   <Text style={styles.text3}>Divina Leitura é um aplicativo feito pela franquia Divino Amor, em Campinas.
    Criado com o intuito de trazer as histórias dos Santos Católicos  e seus respectivos dias.
    Você pode também compartilhar conosco sua experiência com aquele santo de Devoção e a graça alcançada.</Text>

    <Text style={styles.text4}> FAÇA-NOS UMA VISITA: </Text>
      
    </View>

  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F6F6EE'
  },

    text1: {
    textAlign: 'center',
    fontSize: 28,
    height: 125,
    color: '#FFFFFF',
  },
  text2: {
    textAlign: 'center',
    fontSize: 30,
    height: 75,
    color: '#CBAA6C',
    fontWeight: 'bold'
  },
  text3: {
    textAlign: 'center',
    fontSize: 30,
    height: '100%',
    color: '#6F5536',
  },
  text4: {
    textAlign: 'center',
    fontSize: 30,
    height: 20,
    color: '#CBAA6C',
    fontWeight: 'bold'
  }
})
