import React from 'react';
import { View, StyleSheet, Image, Text, ImageBackground } from 'react-native';

export default function HomePage () {
  return (

  
    <View style={styles.container}>

<ImageBackground source={require('../assets/images/loja01.jpg')} style={styles.background}>
      <View style={styles.container}>
        <Text style={styles.text}>Este é o texto sobre a imagem de fundo</Text>
      </View>
    </ImageBackground>

   <Text style={styles.text1}> FIQUE À VONTADE PARA COMPARTILHAS SEUS TESTEMUNHOS! </Text>

   <Text style={styles.text2}> SOBRE O APLICATIVO </Text>

   <Text style={styles.text3}>Divina Leitura é um aplicativo feito pela franquia Divino Amor, em Campinas.
    Criado com o intuito de trazer as histórias dos Santos Católicos  e seus respectivos dias.
    Você pode também compartilhar conosco sua experiência com aquele santo de Devoção e a graça alcançada.</Text>

    <Text style={styles.text2}> FAÇA-NOS UMA VISITA: </Text>
      
    </View>

  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F6F6EE',
  },

    text1: {
    marginVertical: 10,
    textAlign: 'center',
    fontSize: 20,
    height: 'auto',
    color: '#FFFFFF',
  },
  text2: {
    marginVertical: 10,
    textAlign: 'center',
    fontSize: 25,
    height: 'auto',
    color: '#CBAA6C',
    fontWeight: 'bold'
  },
  text3: {
    marginVertical: 10,
    textAlign: 'center',
    fontSize: 20,
    height: 'auto',
    color: '#6F5536',
  }
})
